import React, { useState } from 'react';
import PropTypes from 'prop-types';
import './Login.css';


// async function loginUser(credentials) {
//   return fetch('http://localhost:3001/login', {
//     method: 'POST',
//     headers: {
//       'Content-Type': 'application/json'
//     },
//     body: JSON.stringify(credentials)
//   })
//     .then(data => data.json())
//  }

export default function Login({setToken}) {
  const [username, setUserName] = useState();
  const [password, setPassword] = useState();

  // const handleSubmit = async e => {
  //   e.preventDefault();
  //   const token = await loginUser({
  //     username,
  //     password
  //   });
  //   setToken(token);
  // }
  return(
    <div className="login-wrapper">
      <div id="loginHeading">Login</div>
      <form action="/auth" method="post">
        <label>
          <p>Username</p>
          <input class="formInput" type="text" placeholder='username' onChange={e => setUserName(e.target.value)}/>
        </label>
        <label>
          <p>Password</p>
          <input class="formInput" type="password" placeholder='password' onChange={e => setPassword(e.target.value)}/>
        </label>
        <div id='submitButtonWrapper'>
          <button id="submitBtn" type="submit" value="Login">Submit</button>
        </div>
      </form>
    </div>
  )
}

