import React, { useState } from "react";

import "../styles/PlaceBoard.css";
import PlaceInfoCard from "./placeInfoCard";

export default function PlaceBoard(placeCardInfo, setPlaceCardInfo){
    const [placeCount, setPlaceCount] = useState(placeCardInfo.length);

    function places(){
        placeCardInfo.map((place, index) => {
            return <PlaceInfoCard key={index} placeName={place.placeName} placeDay={place.placeDays} />;
        })
    }

    if(placeCount==0){
        return(
            <>
            <div id="placeBoard">
                Added places will appear here 
            </div>
            </>
        );
    }
    else{
        return(
            <>
            <div id="placeBoard">

            </div>
            </>
        );
    }

}
