import "./Categories.css";

import React from "react";
import NavBar from "./NavBar";
import BottomNav from "./BottomNav";

import img1 from "./assets/categories/historicSitesImg.png";
import img2 from "./assets/categories/bodyRelaxingImg.png";
import img3 from "./assets/categories/religiousSitesImg.png";
import img4 from "./assets/categories/parksAndGardensImg.png";
import img5 from "./assets/categories/outdoorsImg.png";
import img6 from "./assets/categories/museumsImg.png";
import img7 from "./assets/categories/cuisinesImg.png";
import img8 from "./assets/categories/shoppingImg.png";
import img9 from "./assets/categories/funAndGamesImg.png";
import img10 from "./assets/categories/drinksAndDanceImg.png";

const selectedCategories = {
    historicSites: false,
    bodyRelaxing: false,
    religiousSites: false,
    parksAndGardens: false,
    outdoors: false,
    museums: false,
    cuisines: false,
    shopping: false,
    funAndGames: false,
    drinksAndDance: false
}

function selectCategory(e){
    // switch ()
    // selectCategories.
    return (e.target.classList.toggle('activeCard'));
}

function Categories(){
    return (
        <>
        <NavBar page="categories"/>

        <div id="categoriesHeadingWrapper">
            <div id="categoriesHeading">
                Select your interests for this trip
            </div>
        </div>


        <div id="categoryCardsWrapper">
            <div id="categoryCards">

                
                <CategoryCard num={1} text="Historic Sites" />
                <CategoryCard num={2} text="Body Relaxing" />
                <CategoryCard num={3} text="Religious Sites" />
                <CategoryCard num={4} text="Parks and Gardens" />
                <CategoryCard num={5} text="Outdoors" />
                <CategoryCard num={6} text="Museums" />
                <CategoryCard num={7} text="Cuisines" />
                <CategoryCard num={8} text="Shopping" />
                <CategoryCard num={9} text="Fun and Games" />
                <CategoryCard num={10} text="Drinks and Dance" />


                {/* <div class="card" id="card1">
                    <img class="cardImg2" src={img1} onClick={selectCategory}></img>
                    <div class="categoriesFilm" id="film1"></div>
                    <div class="categoriesCardText" id="text1">Historic Sites</div>
                </div>
                <div class="card" id="card2">
                    <img class="cardImg2" src={img2} onClick={selectCategory}></img>
                    <div class="categoriesFilm" id="film1"></div>
                    <div class="categoriesCardText" id="text1">Body Relaxing</div>
                </div>
                <div class="card" id="card3">
                    <img class="cardImg2" src={img3} onClick={selectCategory}></img>
                    <div class="categoriesFilm" id="film1"></div>
                    <div class="categoriesCardText" id="text1">Religious Sites</div>
                </div>
                <div class="card" id="card4">
                    <img class="cardImg2" src={img4} onClick={selectCategory}></img>
                    <div class="categoriesFilm" id="film1"></div>
                    <div class="categoriesCardText" id="text1">Parks and Gardens</div>
                </div>
                <div class="card" id="card5">
                    <img class="cardImg2" src={img5} onClick={selectCategory}></img>
                    <div class="categoriesFilm" id="film1"></div>
                    <div class="categoriesCardText" id="text1">Outdoors</div>
                </div>
                <div class="card" id="card6">
                    <img class="cardImg2" src={img6} onClick={selectCategory}></img>
                    <div class="categoriesFilm" id="film1"></div>
                    <div class="categoriesCardText" id="text1">Museums</div>
                </div>
                <div class="card" id="card7">
                    <img class="cardImg2" src={img7} onClick={selectCategory}></img>
                    <div class="categoriesFilm" id="film1"></div>
                    <div class="categoriesCardText" id="text1">Cuisines</div>
                </div>
                <div class="card" id="card8">
                    <img class="cardImg2" src={img8} onClick={selectCategory}></img>
                    <div class="categoriesFilm" id="film1"></div>
                    <div class="categoriesCardText" id="text1">Shopping</div>
                </div>
                <div class="card" id="card9">
                    <img class="cardImg2" src={img9} onClick={selectCategory}></img>
                    <div class="categoriesFilm" id="film1"></div>
                    <div class="categoriesCardText" id="text1">Fun and Games</div>
                </div>
                <div class="card" id="card10">
                    <img class="cardImg2" src={img10} onClick={selectCategory}></img> 
                    <div class="categoriesFilm" id="film1"></div>
                    <div class="categoriesCardText" id="text1">Drinks and Dance</div>
                </div> */}


            </div>
        </div>

        <BottomNav />
        </>
    );
}

function CategoryCard(props){
    const i = props.num;
    const text = props.text;

    function imgsrc(i){
        switch(i){
            case 1:
                return img1;
            case 2:
                return img2;
            case 3:
                return img3;
            case 4:
                return img4;
            case 5:
                return img5;
            case 6:
                return img6;
            case 7:
                return img7;
            case 8:
                return img8;
            case 9:
                return img9;
            case 10:
                return img10;
        }
    }

    return (
        <>
        <div class="categoryCard" id={"card"+props.num}>
            <img class="cardImg2" src={imgsrc(props.num)} onClick={selectCategory}></img> 
            <div class="categoriesFilm" id="film1"></div>
            <div class="categoriesCardText" id="text1">{props.text}</div>
        </div>
        </>
    );
}

export default Categories;
