import React, { useState } from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import './Login.css';
import { useNavigate } from "react-router-dom";


function loginUser(credentials) {
  return axios.post('/auth', credentials)
    .then(response => response.data);
 }

export default function Login() {
  const [username, setUserName] = useState();
  const [password, setPassword] = useState();

  const navigate = useNavigate();

  const handleSubmit = async e => {
    e.preventDefault();

    console.log('waiting for auth')
    const token = await loginUser({
      username,
      password
    });

    if(token===1){
      const prompt = 'Incorrect Username and/or Password!';
    }
    else if (token===2){
      const prompt = 'Please enter Username and Password!';
    }
    else{
      sessionStorage.setItem('user-token', token);
      navigate('/');
    }
    
  }
  
  return(
    <div className="login-wrapper">
      <div id="loginHeading">Login</div>
      <form onSubmit={handleSubmit}>
        <label>
          <p>Username</p>
          <input class="formInput" type="text" placeholder='username' onChange={e => setUserName(e.target.value)}/>
        </label>
        <label>
          <p>Password</p>
          <input class="formInput" type="password" placeholder='password' onChange={e => setPassword(e.target.value)}/>
        </label>
        <div id='submitButtonWrapper'>
          <button id="submitBtn" type="submit" value="Login">Submit</button>
        </div>
        <label>
          {prompt}
        </label>
      </form>
    </div>
  )
}

